sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

        // var 'rightnow' stores the current time
        var rightnow = new GlideDateTime();
        var toDay = new GlideDateTime()
        //create a GlideDateTime object for the WhenNeeded date
        var whenNeeded = new GlideDateTime(current.u_when_needed);

        //If when needed is before rightnow do not write the record to the dbase. 
        //Output an error message to the screen.
        /*	if(whenNeeded.before(rightnow)) {
        		gs.addErrorMessage("When needed date cannot be in the past. You request has not been written to the database. Please select a diffent date");*/

          // Get the date portion of rightnow and whenNeeded (no timestamp)
        var today = rightnow.getLocalDate();
        var istoday = whenNeeded.getLocalDate();
        // Compare today and istoday to see if they are the same day
        if (today.compareTo(istoday) == 0) {
            gs.addErrorMessage("You cannot submit NeedIt requests for today.");
            current.setAbortAction(true);
        }


})(current, previous);
